mapper.select:
SELECT
	/* selectVnsNumberPrefixCount */ 
	count(*)
FROM 
	TB_PREFIX
	WHERE
		LOGISTICS_CODE = #{logisticsCode}
		AND START_PREFIX <= #{prefix}
		AND END_PREFIX >= #{prefix}

SELECT 
	count(*)
FROM 
	TB_VNS_BIZ_LOGISTICS
WHERE LOGISTICS_CODE = #{logisticsCode}
	AND USER_PWD = #{userPwd}
	AND USE_YN = #{useYn}
	AND BIZ_ID = #{bizId}
